/*eslint-disable */

import { LOGIN_USER, REGISTER_USER, GET_USER, GET_USERS, AUTH_USER } from '../_actions/types'

export default function (state = {}, action) {

    switch (action.type) {
        case LOGIN_USER:
            return { ...state, loginSuccess: action.payload }
            break;
        case REGISTER_USER:
            return { ...state, register: action.payload }
            break;

        case GET_USER:
            return { ...state, getUser: action.payload }
            break;
        case GET_USERS:
            return { ...state, getUsers: action.payload }
            break;
        case AUTH_USER:
            return { ...state, userData: action.payload }
            break;

        default:
            return state;
    }
}
